      $('#wizard-5 .finish').hide();
      $('#wizard-5 .next').show();
      if ($('#wizard-5 .nav li:last-child').hasClass('active')){
        $('#wizard-5 .next').hide();
        $('#wizard-5 .finish').show();
      }
    },
    'onNext': function(){
      scrollTo('#wizard-5',-100);
    },
    'onPrevious': function(){
      scrollTo('#wizard-5',-100);
    }   
  });   
});
</script>
